import React from 'react';
import { useState } from "react";

export default function Carousel({ items }) {
  const [activeIndex, setActiveIndex] = useState(0);

  const handlePrevClick = () => {
    setActiveIndex((activeIndex - 1 + items.length) % items.length);
  };

  const handleNextClick = () => {
    setActiveIndex((activeIndex + 1) % items.length);
  };

  return (
    <div className="relative">
      {items.map((item, index) => (
        <div
          key={index}
          className={`absolute top-0 left-0 w-full h-full ${
            activeIndex === index ? "" : "opacity-0 pointer-events-none"
          } transition-opacity duration-1000`}
        >
          <div className="relative">
            <img src={item.image} alt={`Slide ${index}`} className="w-full" />
            <iframe
              className="absolute top-0 left-0 w-full h-full"
              title={`Slide ${index}`}
              src={item.iframe}
              frameBorder="0"
              allowFullScreen
            />
          </div>
        </div>
      ))}
      <button
        className="absolute top-1/2 left-0 transform -translate-y-1/2 hover:text-gray-700"
        onClick={handlePrevClick}
      >
        &#x2190;
      </button>
      <button
        className="absolute top-1/2 right-0 transform -translate-y-1/2 hover:text-gray-700"
        onClick={handleNextClick}
      >
        &#x2192;
      </button>
    </div>
  );
}
