import React from "react";
import Slider from "react-slick";

const videos = [
  { id: "abc123", title: "Vídeo 1" },
  { id: "def456", title: "Vídeo 2" },
  { id: "ghi789", title: "Vídeo 3" },
];

const CustomDot = ({ index, onClick }) => (
  <div
    onClick={onClick}
    className="relative w-1/2 h-0 pb-3 cursor-pointer z-10"
    style={{ paddingTop: "50%" }}
  >
    <iframe
      src={`https://www.youtube.com/embed/${videos[index].id}`}
      title={`Vídeo ${index + 1}`}
      frameBorder="0"
      allow="autoplay; encrypted-media"
      allowFullScreen
      className="absolute top-0 left-0 w-full h-full"
    ></iframe>
  </div>
);

const Carou = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: "0",
    dotsClass: "slick-dots w-auto bottom-0 mb-8",
    customPaging: (i) => (
      <button className="text-white opacity-50 focus:outline-none focus:opacity-100">
        {i + 1}
      </button>
    ),
    appendDots: (dots) => (
      <div className="px-6">
        <ul>{dots}</ul>
      </div>
    ),
  };

  return (
    <div className="bg-fuchsia-950 py-12">
      <Slider {...settings}>
        {videos.map((video, index) => (
          <CustomDot key={index} index={index} />
        ))}
      </Slider>
    </div>
  );
};

export default Carou;
