import React from 'react';
import { useState } from 'react';

const Flip = ({ title, descriptionFront, descriptionBack, imageSrc }) => {
  const [flipped, setFlipped] = useState(false);

  const flip = () => {
    setFlipped(!flipped);
  };

  return (
    <div
      className={`relative h-[400px] w-[300px] md:h-[300px] md:w-[200px] mx-4 md:mx-0 my-4 transform perspective-1000 ${
        flipped ? 'rotate-y-180' : ''
      }`}
      onClick={flip}
    >
      <div className="absolute w-full h-full bg-white rounded-lg shadow-lg overflow-hidden">
        <div className="front p-6">
          <img
            className="w-32 h-32 object-cover rounded-full mx-auto shadow-lg mb-4"
            src={imageSrc}
            alt={title}
          />
          <p className="text-lg font-semibold mb-2">{title}</p>
          <p className="text-gray-700">{descriptionFront}</p>
        </div>
        <div className="back p-6">
          <p className="text-lg font-semibold mb-2">{title}</p>
          <p className="text-gray-700">{descriptionBack}</p>
        </div>
      </div>
    </div>
  );
};

export default Flip;
