import { useState } from 'react';
const cards = [
  { title: 'Card 1', content: 'Conteúdo do Card 1' },
  { title: 'Card 2', content: 'Conteúdo do Card 2' },
  { title: 'Card 3', content: 'Conteúdo do Card 3' },
];

export default function Card({ title, content }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="flex-1 h-[100%] flex relative self-stretch px-[24px] pt-[30px] flex-col bg-fuchsia-900 rounded-[5px] ">
      <div className="text-lg not-italic font-[700] leading-[33px] mb-[16px] text-[#ffffff]" onClick={() => setIsOpen(!isOpen)}>
        {title}
      </div>
      {isOpen &&
    <>
      <div className="leading-[24px] text-[18px] text-[#ffffff]">{content}</div>
      <a href="whatsapp.html">
       <div className=" mt-[16px] px-[32] py-[16px] mb-[48px] rounded-[56px] flex-col bg-black items-center flex cursor-pointer color-[#ffffff] transition-[0.3s]  ">
        <span className="text-[18px] text-[#ffffff] no-italic font-[600]">SAIBA MAIS!</span>
       </div>
      </a>
    </>
}
    </div>
  );
}
