import React from 'react';

const Flip = ({ title, descriptionFront, descriptionBack, imageSrc }) => {
  const [flipped, setFlipped] = React.useState(false);

  const flip = () => {
    setFlipped(!flipped);
  };

  return (
    <div
      className={`w-[100%] h-[100%] flex z-1 items-center pt-[46px] px-[100px] flex-col bg-fuchsia-950 ${
        flipped ? 'rotate-y-180' : ''
      }`}
    >
      <div className="absolute inset-0 w-full h-full">
        <div className="absolute inset-0 w-full h-full transform rotate-y-180">
          <div className="p-6">
            <p className="text-lg font-semibold mb-2">{title}</p>
            <p className="text-gray-700">{descriptionBack}</p>
          </div>
        </div>
        <div className="absolute inset-0 w-full h-full">
          <div
            className="flex flex-col items-center justify-center p-6 bg-white"
            onClick={flip}
          >
            <img
              className="w-32 h-32 object-cover rounded-full shadow-md mb-4"
              src={imageSrc}
              alt={title}
            />
            <p className="text-lg font-semibold mb-2">{title}</p>
            <p className="text-gray-700">{descriptionFront}</p>
            <button className="text-blue-500 hover:text-blue-600 mt-4 focus:outline-none">
              Saiba mais
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Flip;
