import { useState } from 'react';


export default function Card({ title, content }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="flex-1 h-[100%] flex relative self-stretch px-[24px] pt-[30px] flex-col bg-fuchsia-900 rounded-[5px] hover:border-[#008bf8]
    hover:shadow[0 4px 18px 0 rgba(0, 0, 0, 0.25)]" onClick={() => setIsOpen(!isOpen)}>
      <div className="font-sans text-lg not-italic font-[700] leading-[33px] mb-[16px] text-[#ffffff]" >
        {title}
      </div>
      {isOpen &&
      <>
       <div className="font-sans leading-[24px] text-[18px] text-[#ffffff]">{content}</div>
        <a href="whatsapp.html">
       <div className=" hover:border-[#ffffff] mt-[16px] px-[32] py-[16px] mb-[48px] rounded-[56px] flex-col items-center flex cursor-pointer color-[#ffffff] transition-[0.3s]  ">
          <span className="  text-[18px] text-[#ffffff] font-sans font-[600]">SAIBA MAIS!</span>
       </div>
        </a>
      </>
}
    </div>
  );
}
