import React from 'react';
import { useState } from 'react';

export default function Carousel() {
  const [currentSlide, setCurrentSlide] = useState(0);

  const images = [
    { src: '/image1.jpg', iframeSrc: 'https://www.youtube.com/embed/VIDEO_ID' },
    { src: '/image2.jpg', iframeSrc: 'https://www.youtube.com/embed/VIDEO_ID' },
    { src: '/image3.jpg', iframeSrc: 'https://www.youtube.com/embed/VIDEO_ID' },
    { src: '/image4.jpg', iframeSrc: 'https://www.youtube.com/embed/VIDEO_ID' },
  ];

  const nextSlide = () => {
    setCurrentSlide(currentSlide === images.length - 1 ? 0 : currentSlide + 1);
  };

  const prevSlide = () => {
    setCurrentSlide(currentSlide === 0 ? images.length - 1 : currentSlide - 1);
  };

  return (
    <div className="relative w-full w-[100%] h-[100%]">
      <div className="absolute w-full h-full">
        {images.map((image, index) => (
          <div
            key={index}
            className={`absolute w-full h-full ${
              index === currentSlide ? 'opacity-100' : 'opacity-0'
            } transition-opacity`}
          >
            <img
              src={image.src}
              alt={`Image ${index}`}
              className="w-full h-full object-cover"
            />
            <iframe
              src={image.iframeSrc}
              title={`Iframe ${index}`}
              className="absolute top-0 left-0 w-full h-full"
            />
          </div>
        ))}
      </div>
      <button onClick={prevSlide}>Anterior</button>
      <button onClick={nextSlide}>Pr√≥ximo</button>
    </div>
  );
}
