import React, { useState } from 'react';

function Carousel(props) {
  const [selectedItemIndex, setSelectedItemIndex] = useState(0);

  function handleItemSelection(index) {
    setSelectedItemIndex(index);
  }

  return (
    <div className="relative">
      <div className="carousel flex items-center justify-center">
        {props.items.map((item, index) => (
          <div
            key={index}
            className={`relative h-64 w-64 md:h-80 md:w-80 lg:h-96 lg:w-96 ${
              index === selectedItemIndex ? 'scale-110' : ''
            }`}
          >
            <img
              className="absolute top-0 left-0 w-full h-full object-cover"
              src={item.imageSrc}
              alt={item.imageAlt}
            />
            <button
              className="absolute inset-0 focus:outline-none"
              onClick={() => handleItemSelection(index)}
            >
              <div className="w-full h-full bg-black opacity-25 hover:opacity-75 transition-opacity duration-300" />
            </button>
          </div>
        ))}
      </div>
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="z-10 w-full max-w-2xl p-8">
          <iframe
            className="w-full h-full rounded-lg shadow-lg"
            src={props.items[selectedItemIndex].iframeSrc}
            title={props.items[selectedItemIndex].iframeTitle}
            allowFullScreen
          />
        </div>
      </div>
    </div>
  );
}

export default Carousel;
