import Nav from '../../componenents/nav';
import Flutuante from '../../componenents/micros/flutter';
import Formm from '../../componenents/form';
import Footer from '../../componenents/footer';
import Promo from '../../componenents/promo/promo';
import Thanks from '../../componenents/thanks';
import { useState } from 'react';

export default function Form() {
  const [showForm, setShowForm] = useState(true);

  const handleSubmit = () => {
    // Lógica para lidar com a submissão do formulário

    // Depois que a ação é executada com sucesso, altera o estado para ocultar o formulário e exibir a mensagem de agradecimento
    setShowForm(false);
  };

  return (
    <div className="App  ">
      <Promo />
      <Nav />
      {showForm ? (
        <Formm handleSubmit={handleSubmit} />
      ) : (
        <Thanks />
      )}
      <Footer />
      <Flutuante />
    </div>
  );
}
