const flip = [
  {
    Ftitle: 'Necessidade1',
    Btitle: 'Tráfego pago',
    Ftext: 'Quero que conheçam meu produto ',
    Btext: ' Ajuda a Veicular o anúncio do seu Produto pelo Facebook,Instagram ou site do Google trazendo pessoais interessadas no seu produto. ',
  },
  {
    Ftitle: 'Necessidade2',
    Btitle: ' Website ',
    Ftext: 'Quero vender mais',
    Btext: 'Pra essa necessidade há vantagens em fazer um Website para facilitar suas vendas, fazer tráfego para veicular esse site e atrair possíveis compradores.Temos também o WhatsApp Business que é uma forma prática de vender , onde o seu cliente compra diretamente com você sem qualquer problema.',
  },
];
const flip2 = [
  {
    Ftitle: 'Necessidade3',
    Btitle: ' WhatsApp Business',
    Ftext: 'Não tenho tempo para lidar com atendimento ao cliente',
    Btext: 'Podemos fazer através do APP do WhatsApp Business, a automação de mensagens , onde o cliente vai te dar todas as informações necessárias sem você trocar mensagens manuais com ele . ',
  },
  {
    Ftitle: 'Necessidade4',
    Btitle: 'Fabricando Digital ',
    Ftext: ' Não tenho loja,instagram, página de vendas ou WhatsApp Business',
    Btext: 'Com os nossos serviços, você adquiri o seu Instagram Profissional, e  mesmo, temos também a Gestão de WhatsApp Business onde você poupa tempo de atendimento, podendo vender com praticidade pelo WhatsApp e a Criação de Landing Pages e Sites Autorais , mostrando o seu serviço/produto de maneira Prática e Objetiva',
  },
];

export default function Fliper() {
  return (
    <section className=" h-[100%] flex z-1 items-center pt-[46px] flex-col be">
      <Title title={'Qual a sua necessidade ?'} />
      <div className=" flex max-w-7xl items-center  grid-rows-4 gap-[5px] flex-col sm:gap-[40px] sm:flex-row ">
        <div className="  flex items-center mb-[32px]  gap-[40px] flex-col sm:flex-row ">
          {flip.map((card, index) => (
            <Flip
              key={index}
              Ftitle={card.Ftitle}
              Ftext={card.Ftext}
              Btitle={card.Btitle}
              Btext={card.Btext}
            />
          ))}
        </div>
        <div className="  flex items-center mb-[32px] gap-[40px] flex-col sm:flex-row ">
          {flip2.map((card, index) => (
            <Flip
              key={index}
              Btitle={card.Btitle}
              Btext={card.Btext}
              Ftitle={card.Ftitle}
              Ftext={card.Ftext}
            />
          ))}
        </div>
      </div>
    </section>
  );
}
