import React, { useState } from 'react';
import './styles.css';

export default function Flip({ Ftext, Ftitle, Btext, Btitle }) {
  const [flip, setFlip] = useState(false);

  const handleFlip = () => {
    setFlip(!flip);
  };

  return (
    <div className="flip-card" onClick={handleFlip}>
      <div className={`flip-card-inner ${flip ? 'flip-card-flip' : ''}`}>
        <div className="flip-card-front">
          <p className="title">{Ftitle}</p>
          <p>{Ftext}</p>
          <button onClick={(e) => e.stopPropagation()}>Não vira</button>
        </div>
        <div className="flip-card-back">
          <p className="title">{Btitle}</p>
          <p>{Btext}</p>
          <button onClick={(e) => e.stopPropagation()}>Não vira</button>
        </div>
      </div>
    </div>
  );
}
