import { useEffect, useState } from 'react';
import './styles.css';

export default function Flutuante() {
  const [isVisible, setIsVisible] = useState(false);

  // Mostra ou oculta o botão dependendo do scroll
  const toggleVisibility = () => {
    if (window.pageYOffset > 300) {
      setIsVisible(true);
    } else {
      setIsVisible(false);
    }
  };

  // Adiciona um listener para o scroll quando o componente é montado
  useEffect(() => {
    window.addEventListener('scroll', toggleVisibility);
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  // Faz o scroll para o topo da página
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };
  return (
    <div>
      <div className="whats">
        <a
          href="https://api.whatsapp.com/send/?phone=5521979762621&text&type=phone_number&app_absent=0"
          className="z-[999]"
          target="_blank"
          rel="noreferrer"
        >
          <img
            src={`${process.env.PUBLIC_URL}/assets/iconzap.PNG`}
            alt="fale conosco pelo Whatsapp"
            title="fale conosco pelo Whatsapp"
          />
        </a>
      </div>
{/*       {isVisible && ( */}
        <div className="whats" onClick={scrollToTop}>
          <a
            href="https://api.whatsapp.com/send/?phone=5521979762621&text&type=phone_number&app_absent=0"
            className="z-[999]"
            target="_blank"
            rel="noreferrer"
          >
            <img
              src={`${process.env.PUBLIC_URL}/assets/iconzap.PNG`}
              alt="fale conosco pelo Whatsapp"
              title="fale conosco pelo Whatsapp"
            />
          </a>
        </div>
   {/*    )} */}
    </div>
  );
}
